const { locale, setLocale, localePath } = useI18n();

const changeLang = async () => {
    locale.value = locale.value === "en" ? "ar" : "en";
    if (locale.value === "ar") {
        document.documentElement.lang = "ar";
        document.documentElement.dir = "rtl";
